'use client'

import NoticeBoardList from '@/app/[locale]/(site)/home/main/_cpnt/NoticeBoardList'
import { STAFF_PATH } from '@/app/site-path'
import useTranslation from '@/localization/client/useTranslations'
import Link from 'next/link'
import { useRouter } from 'next/navigation'
import { CSSProperties } from 'react'
import { useOnLoadBoardNoticeList } from '@/client/store/home/hook'

const ICON_WRITE =
  'data:image/png;base64,'

const styleWriteContainer: CSSProperties = {
  height: '40px',
  display: 'flex',
  flexDirection: 'row-reverse',
  marginBottom: '10px',
}
const styleButton: CSSProperties = {
  display: 'flex',
  cursor: 'pointer',
  backgroundColor: 'var(--blue)',
  borderRadius: '8px',
  padding: '0 20px',
  alignItems: 'center',
}
const styleButtonImage: CSSProperties = {
  display: 'inline',
  width: '24px',
  height: '24px',
  alignItems: 'center',
  marginRight: '0.2em',
}
const styleButtonText: CSSProperties = {
  display: 'flex',
  color: 'white',
  fontSize: 'var(--text-l)',
}

export default function Page({
  searchParams,
}: {
  searchParams: { page: string }
}) {
  const page = searchParams.page ? Number(searchParams.page) : 1

  // @Language 'common'
  const { t } = useTranslation()

  const route = useRouter()

  const { payload, loading, error } = useOnLoadBoardNoticeList({ page })

  const noticeList = payload
    ? [
        ...payload.board.map((board) => {
          return {
            title: board.title,
            date: board.registDate.split('T')[0],
            link: `${STAFF_PATH.NOTICE.MAIN}/${board.notifyId}`,
          }
        }),
      ]
    : []
  const maxPage = payload?.page?.totalPages || 0
  const isWritable = payload.writable

  const onPageChange = (page: number) => {
    route.push(`${STAFF_PATH.NOTICE.MAIN}?page=${page}`)
  }

  return (
    <>
      {isWritable && (
        <div style={styleWriteContainer}>
          <Link href={STAFF_PATH.NOTICE.WRITE} style={styleButton}>
            <img style={styleButtonImage} src={ICON_WRITE} />
            <span style={styleButtonText}>{t('t925')}</span>
          </Link>
        </div>
      )}
      <NoticeBoardList
        list={noticeList}
        currentPage={page}
        maxPage={maxPage}
        onPageChange={onPageChange}
      />
    </>
  )
}
